import { Component } from '@angular/core';
import { TareaService } from '../../services/tarea.service';
import { Tarea } from '../../models/tarea.model';

@Component({
  selector: 'app-tarea-form',
  templateUrl: './tarea-form.component.html'
})
export class TareaFormComponent {
  tarea: Tarea = {
    titulo: '',
    descripcion: '',
    fechaLimite: '',
    completada: false,
    prioridad: 0,
    categoria: ''
  };

  constructor(private tareaService: TareaService) {}

  crearTarea() {
    this.tareaService.crearTarea(this.tarea).subscribe({
      next: () => {
        alert('Tarea creada con éxito');
        // Limpiar el formulario
        this.tarea = {
          titulo: '',
          descripcion: '',
          fechaLimite: '',
          completada: false,
          prioridad: 0,
          categoria: ''
        };
      },
      error: err => {
        alert('Error al crear la tarea');
        console.error(err);
      }
    });
  }
}
<h2>Nueva Tarea</h2>
<form (ngSubmit)="crearTarea()" #tareaForm="ngForm">
  <label>
    Título:
    <input type="text" [(ngModel)]="tarea.titulo" name="titulo" required>
  </label><br><br>

  <label>
    Descripción:
    <textarea [(ngModel)]="tarea.descripcion" name="descripcion"></textarea>
  </label><br><br>

  <label>
    Fecha límite:
    <input type="date" [(ngModel)]="tarea.fechaLimite" name="fechaLimite" required>
  </label><br><br>

  <label>
    Completada:
    <input type="checkbox" [(ngModel)]="tarea.completada" name="completada">
  </label><br><br>

  <label>
    Prioridad:
    <select [(ngModel)]="tarea.prioridad" name="prioridad" required>
      <option [value]="0">Baja</option>
      <option [value]="1">Media</option>
      <option [value]="2">Alta</option>
    </select>
  </label><br><br>

  <label>
    Categoría:
    <input type="text" [(ngModel)]="tarea.categoria" name="categoria">
  </label><br><br>

  <button type="submit">Guardar</button>
</form>
