import { Component, OnInit } from '@angular/core';
import { TareaService } from '../../services/tarea.service';
import { Tarea } from '../../models/tarea.model';

@Component({
  selector: 'app-tarea-list',
  templateUrl: './tarea-list.component.html'
})
export class TareaListComponent implements OnInit {
  tareas: Tarea[] = [];

  constructor(private tareaService: TareaService) {}

  ngOnInit() {
    this.obtenerTareas();
  }

  obtenerTareas() {
    this.tareaService.getTareas().subscribe(data => {
      this.tareas = data;
    });
  }

  marcarComoCompletada(tarea: Tarea) {
    const tareaActualizada = { ...tarea, completada: true };
    this.tareaService.actualizarTarea(tarea.id!, tareaActualizada).subscribe({
      next: () => {
        alert('La tarea fue marcada como completada.');
        this.obtenerTareas();  // Recargar la lista actualizada
      },
      error: err => {
        console.error('Error al actualizar la tarea:', err);
        alert('Ocurrió un error al marcar la tarea como completada.');
      }
    });
  }
}
<h2>Lista de Tareas</h2>
<table>
  <tr>
    <th>Título</th>
    <th>Fecha límite</th>
    <th>Completada</th>
    <th>Prioridad</th>
  </tr>
  <tr *ngFor="let tarea of tareas">
    <td>{{ tarea.titulo }}</td>
    <td>{{ tarea.fechaLimite | date }}</td>
    <td>{{ tarea.completada ? 'Sí' : 'No' }}</td>
    <td>{{ tarea.prioridad }}</td>
    <td>
        <button 
            *ngIf="!tarea.completada"
            (click)="marcarComoCompletada(tarea)">
            Marcar como completada
        </button>
    </td>
</tr>
</table>
